INSERT INTO quick_alert_rule_metric (name,pql,unit,"group") VALUES
	 ('平均请求延时','sum by (svc_name, content_key) (increase(kindling_span_trace_duration_nanoseconds_sum[1m]))/ sum by (svc_name, content_key) (increase(kindling_span_trace_duration_nanoseconds_count[1m]))/1000000','s','app'),
	 ('请求错误率','sum by (svc_name, content_key) (increase(kindling_span_trace_duration_nanoseconds_count{is_error="true"}[1m]))/ sum by (svc_name, content_key) (increase(kindling_span_trace_duration_nanoseconds_count[1m]))','%','app'),
	 ('磁盘使用率','((node_filesystem_avail_bytes * 100) / node_filesystem_size_bytes and ON (instance_name, device, mountpoint) node_filesystem_readonly == 0) * on(instance_name) group_left (nodename) node_uname_info{nodename=~".+"}','%','infra'),
	 ('网络入吞吐量','(sum by (instance_name) (rate(node_network_receive_bytes_total[2m])) / 1024 / 1024) *  on(instance_name) group_left (nodename) node_uname_info{nodename=~".+"}','MB','infra'),
	 ('磁盘读速度','(sum by (instance_name) (rate(node_disk_read_bytes_total[2m])) / 1024 / 1024) * on(instance_name) group_left (nodename) node_uname_info{nodename=~".+"}','MB/s','infra'),
	 ('磁盘写速度','(sum by (instance_name) (rate(node_disk_written_bytes_total[2m])) / 1024 / 1024 ) * on(instance_name) group_left (nodename) node_uname_info{nodename=~".+"}','MB/s','infra'),
	 ('CPU负载','(sum by (instance_name) (avg by (mode, instance_name) (rate(node_cpu_seconds_total{mode!="idle"}[2m])))) * on(instance_name) group_left (nodename) node_uname_info{nodename=~".+"}','core','infra'),
	 ('CPU IO Wait','(avg by (instance_name) (rate(node_cpu_seconds_total{mode="iowait"}[5m])) * 100) * on(instance_name) group_left (nodename) node_uname_info{nodename=~".+"}','%','infra'),
	 ('磁盘IO利用率','(rate(node_disk_io_time_seconds_total[1m])) * on(instance_name) group_left (nodename) node_uname_info{nodename=~".+"}','%','infra'),
	 ('网络RTT延时','kindling_network_rtt{} * 1000','ms','infra'),
	 ('容器上次运行时间','container_last_seen','s','container'),
	 ('容器持久卷使用率','(sum(container_fs_inodes_free{name!=""}) BY (instance_name) / sum(container_fs_inodes_total) BY (instance_name)) * 100','%','container'),
	 ('容器CPU使用率','(sum(rate(container_cpu_usage_seconds_total{container!=""}[5m])) by (pod, container) / sum(container_spec_cpu_quota{container!=""}/container_spec_cpu_period{container!=""}) by (pod, container) * 100)','%','container'),
	 ('容器cpu_cfs_throttled','sum(increase(container_cpu_cfs_throttled_periods_total{container!=""}[5m])) by (container, pod, namespace) / sum(increase(container_cpu_cfs_periods_total[5m])) by (container, pod, namespace)','%','container'),
	 ('容器内存使用率','(sum(container_memory_working_set_bytes{name!=""}) BY (instance_name, name) / sum(container_spec_memory_limit_bytes > 0) BY (instance_name, name) * 100)','%','container'),
	 ('容器 CPU 利用率(system)','irate(container_cpu_system_seconds_total{image!="", image!~".*pause.*"}[3m]) * 100','%','container'),
	 ('容器 CPU 利用率(user)','irate(container_cpu_user_seconds_total{image!="", image!~".*pause.*"}[3m]) * 100','%','container'),
	 ('容器 CPU 利用率(整体，值不会大于 100)','sum( irate(container_cpu_usage_seconds_total{image!="", image!~".*pause.*"}[3m]) ) by (pod,namespace,container,image) / sum( container_spec_cpu_quota/container_spec_cpu_period ) by (pod,namespace,container,image)','%','container'),
	 ('容器 CPU 利用率(整体，值可能会大于 100)','irate(container_cpu_usage_seconds_total{image!="", image!~".*pause.*"}[3m]) * 100','%','container'),
	 ('容器 CPU 每秒有多少 period','irate(container_cpu_cfs_periods_total{}[3m])','period/s','container'),
	 ('容器 CPU 每秒被 throttle 的 period 量','irate(container_cpu_cfs_throttled_periods_total{}[3m])','period/s','container'),
	 ('容器 CPU 被 throttle 的比例','irate(container_cpu_cfs_throttled_periods_total{}[3m]) / irate(container_cpu_cfs_periods_total{}[3m]) * 100','%','container'),
	 ('容器 filesystem 使用率','container_fs_usage_bytes / container_fs_limit_bytes * 100','%','container'),
	 ('容器 filesystem 使用量','container_fs_usage_bytes','byte','container'),
	 ('容器 filesystem 当前 IO 次数','container_fs_io_current','%','container'),
	 ('容器 filesystem 当前 IO 次数','container_fs_io_current','number','container'),
	 ('容器 filesystem 总量','container_fs_limit_bytes','byte','container'),
	 ('容器 inode free 量','container_fs_inodes_free','SI short','container'),
	 ('容器 inode total 量','container_fs_inodes_total','SI short使用 SI 标准换算, 比如 1K=1000','container'),
	 ('容器 inode 使用率','100 - container_fs_inodes_free / container_fs_inodes_total * 100','%','container'),
	 ('容器 IO 每秒写入 byte 量','sum(irate(container_fs_writes_bytes_total[3m])) by (namespace, pod)','byte','container'),
	 ('容器 IO 每秒读取 byte 量','sum(irate(container_fs_reads_bytes_total[3m])) by (namespace, pod)','byte','container'),
	 ('容器 memory cache 量','container_memory_cache{image!="", image!~".*pause.*"}','byte','container'),
	 ('容器 memory 使用率(Usage)','100 * container_memory_usage_bytes/container_spec_memory_limit_bytes and container_spec_memory_limit_bytes != 0','%','container'),
	 ('容器 memory 使用率(Working Set)','100 * container_memory_working_set_bytes/container_spec_memory_limit_bytes and container_spec_memory_limit_bytes != 0','%','container'),
	 ('容器 memory 使用量(mapped_file)','container_memory_mapped_file{image!="", image!~".*pause.*"}','byte','container'),
	 ('容器 memory 使用量(RSS)','container_memory_rss{image!="", image!~".*pause.*"}','byte','container'),
	 ('容器 memory 使用量(RSS)','','byte','container'),
	 ('容器 memory 使用量(Swap)','container_memory_swap{image!="", image!~".*pause.*"}','byte','container'),
	 ('容器 memory 使用量(Usage)','container_memory_usage_bytes{image!="", image!~".*pause.*"}','byte','container'),
	 ('容器 memory 使用量(Working Set)','container_memory_working_set_bytes{image!="", image!~".*pause.*"}','byte','container'),
	 ('容器 memory 分配失败次数(每秒)','rate(container_memory_failures_total{}[3m])','number','container'),
	 ('容器 memory 限制量','container_spec_memory_limit_bytes{image!="", image!~".*pause.*"}','byte','container'),
	 ('容器 net 每秒发送 bit 量','sum(irate(container_network_transmit_bytes_total[3m])) by (namespace, pod) * 8','bit/s','container'),
	 ('容器 net 每秒发送数据包数量','irate(container_network_transmit_packets_total[3m])','SI short使用 SI 标准换算, 比如 1K=1000','container'),
	 ('容器 net 每秒发送时 drop 包数量','irate(container_network_transmit_packets_dropped_total[3m])','SI short使用 SI 标准换算, 比如 1K=1000','container'),
	 ('容器 net 每秒发送错包数','irate(container_network_transmit_errors_total[3m])','SI short使用 SI 标准换算, 比如 1K=1000','container'),
	 ('容器 net 每秒接收 bit 量','sum(irate(container_network_receive_bytes_total[3m])) by (namespace, pod) * 8','bit/s','container'),
	 ('容器 net 每秒接收数据包数量','irate(container_network_receive_packets_total[3m])','SI short使用 SI 标准换算, 比如 1K=1000','container'),
	 ('容器 net 每秒接收时 drop 包数量','irate(container_network_receive_packets_dropped_total[3m])','SI short使用 SI 标准换算, 比如 1K=1000
